import formataMoedaMixin from '@/mixins/formataMoedaMixin';
import Form from "@/services/modules/form";
import { mapGetters } from 'vuex';
import ModalItem from '../ArquivosDiversos/Modal/ModalItem.vue';
import formataUnidadeMedidaMixin from "@/mixins/formataUnidadeMedidaMixin";
import SnackBarDelete from '@/components/SnackBar/SnackBarDelete.vue';

export default {
	name: "DetalhesAdicao",

	mixins: [
		formataMoedaMixin,
		formataUnidadeMedidaMixin
	],

	components: {
		ModalItem,
		SnackBarDelete,
	},

	props: ["adicaoEscolhida"],

	data() {
		return {
			mostra_cide: false,
			mostra_antidumping: false,
			icms_aux: [],
			currency_config: {
				decimal: ",",
				thousands: ".",
				decimalLength: 5,
				masked: false,
				allowBlank: false,
			},
			antidumping_campo: {
				decimal: ",",
				thousands: ".",
				autoDecimalMode: true,
				prefix: "R$ ",
				decimalLength: 2,
				masked: true,
				valueAsInteger: false,
				allowBlank: false,
			},
			currency_config_valor_moeda: {
				decimal: ",",
				thousands: ".",
				autoDecimalMode: true,
				decimalLength: 2,
				masked: true,
				valueAsInteger: false,
				allowBlank: false,
			},
			currency_config_peso_unitario: {
				decimal: ",",
				thousands: ".",
				autoDecimalMode: true,
				decimalLength: 9,
				masked: true,
				valueAsInteger: false,
				allowBlank: false,
			},
			currency_config_valor_unitario: {
				decimal: ",",
				thousands: ".",
				autoDecimalMode: true,
				decimalLength: 7,
				masked: true,
				valueAsInteger: false,
				allowBlank: false,
			},
			mostraLinha: false,
			handleAbrirItem: false,
			model: null,
			loader: null,
			loadingAdicaoEditar: false,
			fecharPesquisa: true,
			itemDefault: {
				id: "",
				id_adicao: "",
				descricao: "",
				quantidade: "",
				peso_unitario: "",
				valor_unitario: "",
				vmcv: "",
				antidumping: "",
				vucv: "",
				cide_combustiveis: {
					valor: ""
				}
			},
			selected: [],
			historico: [],
			search: "",
			dialogDelete: false,
			dialogDeleteAdicao: false,
			editedIndex: -1,
			excluirMultiplos: false,
		};
	},

	validations: {
		"dados.descricao": {
			checked() {
				if (this.consulta.bl_xml) {
					return true;
				}
				let descricao = this.dados.descricao;
				if (descricao == null || descricao == "") {
					return false;
				} else {
					return true;
				}
			}
		},
		"dados.peso_unitario": {
			checked() {
				if (this.consulta.bl_xml) {
					return true;
				}
				let peso_unitario = this.dados.peso_unitario;
				if (peso_unitario == null || peso_unitario == "") {
					return false;
				} else {
					return true;
				}
			}
		},
		"dados.valor_unitario": {
			checked() {
				if (this.consulta.bl_xml) {
					return true;
				}
				let valor_unitario = this.dados.valor_unitario;
				if (valor_unitario == null || valor_unitario == "") {
					return false;
				} else {
					return true;
				}
			}
		},
		"dados.quantidade": {
			checked() {
				if (this.consulta.bl_xml) {
					return true;
				}
				let quantidade = this.dados.quantidade;
				if (quantidade == null || quantidade == "") {
					return false;
				} else {
					return true;
				}
			}
		},
		"dados.cide_combustiveis.valor": {
			checked() {
				if (this.consulta.bl_xml) {
					return true;
				}
				if (
					this.adicaoEscolhida.tributacoes.cide_combustiveis == null ||
					this.adicaoEscolhida.tributacoes.cide_combustiveis == "" ||
					this.adicaoEscolhida.tributacoes.cide_combustiveis.valor == null ||
					this.adicaoEscolhida.tributacoes.cide_combustiveis.valor == ""
				) {
					return true;
				} else {
					let cideValor = this.dados.cide_combustiveis.valor;
					if (cideValor == null || cideValor == "") {
						return false;
					} else {
						return true;
					}
				}
			}
		},
		"adicaoEscolhida.tributacoes.icms.aliquota_icms": {
			checked() {
				if (!this.consulta.bl_xml) {
					return true;
				}
				return this.icmsAliquota != null;
			}
		},
		"adicaoEscolhida.tributacoes.fundo_combate_pobreza.aliquota": {
			checked() {
				if (!this.consulta.bl_xml) {
					return true;
				}
				return this.adicaoEscolhida.tributacoes.fundo_combate_pobreza.aliquota != null;
			}
		},
		"adicaoEscolhida.tributacoes.icms.reducao_base_calculo": {
			checked() {
				if (this.adicaoEscolhida.tributacoes.icms.id_tipo_tributacao != 10) {
					return true;
				}
				if (!this.consulta.bl_xml) {
					return true;
				}
				return this.adicaoEscolhida.tributacoes.icms.reducao_base_calculo != '' && this.adicaoEscolhida.tributacoes.icms.reducao_base_calculo != null;
			}
		},
		"adicaoEscolhida.tributacoes.icms.aliquota_icms_st": {
			checked() {
				if (this.adicaoEscolhida.tributacoes.icms.id_tipo_tributacao != 7) {
					return true;
				}
				if (!this.consulta.bl_xml) {
					return true;
				}
				return this.adicaoEscolhida.tributacoes.icms.aliquota_icms_st > 0;
			}
		},
		"adicaoEscolhida.tributacoes.icms.base_calculo_st": {
			checked() {
				if (this.adicaoEscolhida.tributacoes.icms.id_tipo_tributacao != 7) {
					return true;
				}
				if (!this.consulta.bl_xml) {
					return true;
				}
				return this.adicaoEscolhida.tributacoes.icms.base_calculo_st > 0;
			}
		},
		"adicaoEscolhida.tributacoes.icms.cod_cest": {
			checked() {
				if (this.adicaoEscolhida.tributacoes.icms.id_tipo_tributacao != 7) {
					return true;
				}
				if (!this.consulta.bl_xml) {
					return true;
				}
				return this.adicaoEscolhida.tributacoes.icms.cod_cest != '' && this.adicaoEscolhida.tributacoes.icms.cod_cest != null;
			}
		},
	},

	computed: {
		blExibeFundo() {
			return this.aliquotas.ICMS.FUNDO && this.aliquotas.ICMS.FUNDO.alertas.length > 0;
		},
		cideErrors() {
			const errors = []
			if (!this.$v["dados.cide_combustiveis.valor"].$dirty) return errors;
			!this.$v["dados.cide_combustiveis.valor"].checked && errors.push('O valor de CIDE é de preenchimento obrigatório')
			return errors;
		},
		descricaoErrors() {
			const errors = []
			if (!this.$v["dados.descricao"].$dirty) return errors;
			!this.$v["dados.descricao"].checked && errors.push('A descrição do item é obrigatória.')
			return errors;
		},
		pesoUnitarioErrors() {
			const errors = []
			if (!this.$v["dados.peso_unitario"].$dirty) return errors;
			!this.$v["dados.peso_unitario"].checked && errors.push('O valor de peso unitário é obrigatório.')
			return errors;
		},
		valorUnitarioErrors() {
			const errors = []
			if (!this.$v["dados.valor_unitario"].$dirty) return errors;
			!this.$v["dados.valor_unitario"].checked && errors.push('O valor unitário do item é obrigatório')
			return errors;
		},
		quantidadeErrors() {
			const errors = []
			if (!this.$v["dados.quantidade"].$dirty) return errors;
			!this.$v["dados.quantidade"].checked && errors.push('O valor de quantidade é obrigatório.')
			return errors;
		},
		icmsAliquotaErrors() {
			const errors = []
			if (!this.$v["adicaoEscolhida.tributacoes.icms.aliquota_icms"].$dirty) return errors;
			!this.$v["adicaoEscolhida.tributacoes.icms.aliquota_icms"].checked && errors.push('O valor da alíquota é obrigatório.')
			return errors;
		},
		fundoAliquotaErrors() {
			const errors = []
			if (!this.$v["adicaoEscolhida.tributacoes.fundo_combate_pobreza.aliquota"].$dirty) return errors;
			!this.$v["adicaoEscolhida.tributacoes.fundo_combate_pobreza.aliquota"].checked && errors.push('O valor do fundo é obrigatório.')
			return errors;
		},
		reducaoBaseCalculoErros() {
			const errors = []
			if (!this.$v["adicaoEscolhida.tributacoes.icms.reducao_base_calculo"].$dirty) return errors;
			!this.$v["adicaoEscolhida.tributacoes.icms.reducao_base_calculo"].checked && errors.push('O valor da redução é obrigatório.')
			return errors;
		},
		aliquotaIcmsStErros() {
			const errors = []
			if (!this.$v["adicaoEscolhida.tributacoes.icms.aliquota_icms_st"].$dirty) return errors;
			!this.$v["adicaoEscolhida.tributacoes.icms.aliquota_icms_st"].checked && errors.push('O valor da alíquota é obrigatório.')
			return errors;
		},
		baseCalculoStErros() {
			const errors = []
			if (!this.$v["adicaoEscolhida.tributacoes.icms.base_calculo_st"].$dirty) return errors;
			!this.$v["adicaoEscolhida.tributacoes.icms.base_calculo_st"].checked && errors.push('O valor da base de cálculo é obrigatório.')
			return errors;
		},
		codCestErros() {
			const errors = []
			if (!this.$v["adicaoEscolhida.tributacoes.icms.cod_cest"].$dirty) return errors;
			!this.$v["adicaoEscolhida.tributacoes.icms.cod_cest"].checked && errors.push('O valor de quantidade é obrigatório.')
			return errors;
		},
		...mapGetters("consulta", { consulta: "getState" }),
		...mapGetters("adicoes", { adicoes: "getState" }),
		...mapGetters("itens", { listaItens: "getListaItens" }),
		...mapGetters("informacoesPadrao", { lista_unidade_medida: "getUnidadeMedida" }),
		...mapGetters("adicao", { adicao: "getState" }),
		...mapGetters("informacoesPadrao", { tipo_tributacao: "getTipoTributacao" }),
		...mapGetters("aliquotas", { aliquotas: "getState" }),
		fundoAliquotas: {
			get() {
				let fundo = [0];
				if (this.aliquotas.ICMS.FUNDO && this.aliquotas.ICMS.FUNDO.aliquota.length > 0) {
					fundo = this.aliquotas.ICMS.FUNDO.aliquota;
				}
				return fundo;
			},
			set(fundo) {
				return fundo;
			}
		},
		fundoAliquota: {
			get() {
				return this.adicaoEscolhida.tributacoes.fundo_combate_pobreza.aliquota;
			},
			set(fundo) {
				if (fundo && fundo.toString().indexOf(',') != -1) {
					fundo = fundo.replace('.', '');
					fundo = fundo.replace(',', '.');
				}
				this.adicaoEscolhida.tributacoes.fundo_combate_pobreza.aliquota = fundo;
			}
		},
		icmsAliquotas: {
			get() {
				return (this.aliquotas.ICMS.ICMS && this.aliquotas.ICMS.ICMS.aliquota.length > 0) ?
					this.aliquotas.ICMS.ICMS.aliquota :
					[0];
			},
			set(icms) {
				return icms;
			}
		},
		icmsAliquota: {
			get() {
				return this.adicaoEscolhida.tributacoes.icms.aliquota_icms;
			},
			set(icms) {
				if (icms && icms.toString().indexOf(',') != -1) {
					icms = icms.replace('.', '');
					icms = icms.replace(',', '.');
				}
				this.adicaoEscolhida.tributacoes.icms.aliquota_icms = icms;
				this.$store.commit('adicao/setIcmsAliquota', icms);
			}
		},
		icmsTipoTributacao: {
			get() {
				return this.adicaoEscolhida.tributacoes.icms.id_tipo_tributacao;
			},
			set(tipo) {
				this.adicaoEscolhida.tributacoes.icms.id_tipo_tributacao = tipo;
			}
		},
		icmsAlerta() {
			return this.aliquotas.ICMS && this.aliquotas.ICMS.alertas ? this.aliquotas.ICMS.alertas.join('<br>') : null;
		},
		handleModalAdicao: {
			get() {
				return this.$store.state.modalAdicoes.modalAdicoes;
			},
			set(newValue) {
				if (newValue != this.handleModalAdicao) {
					this.$store.commit("modalAdicoes/setModalAdicoes", newValue);
				}
			},
		},
		cestItens() {
			return this.aliquotas.ICMS.ST && this.aliquotas.ICMS.ST.cest ? this.aliquotas.ICMS.ST.cest : [this.adicao.tributacoes.icms.cod_cest];
		},
		descricaoIncoterm() {
			let incoterm = {
				1: this.$CFR.TYPE,
				2: this.$CPT.TYPE,
				3: this.$CIF.TYPE,
				4: this.$CIP.TYPE,
				5: this.$DAP.TYPE,
				6: this.$DDP.TYPE,
				7: this.$DPU.TYPE,
				8: this.$EXW.TYPE,
				9: this.$FCA.TYPE,
				10: this.$FAS.TYPE,
				11: this.$FOB.TYPE
			};
			return incoterm[this.adicaoEscolhida.id_incoterm] || '-';
		},

		blExibeCide() {
			return this.adicaoEscolhida.tributacoes?.cide_combustiveis && parseFloat(this.adicaoEscolhida.tributacoes.cide_combustiveis.valor) > 0;
		},

		blExibeAntidumping() {
			return parseFloat(this.adicaoEscolhida.antidumping) > 0;
		},

		headers_xml() {
			let itens = [
				{ text: "Descrição do Item", align: "start", sortable: false, value: "descricao", width: '48%' },
				{ text: "Peso Unitário", align: "start", sortable: false, value: "peso_unitario" }
			];
			if (this.blExibeCide) {
				itens.push({ text: "Cide Combustiveis", align: "start", sortable: false, value: "cide_combustiveis.valor" });
			}
			if (this.blExibeAntidumping) {
				itens.push({ text: "Antidumping", align: "start", sortable: false, value: "antidumping" });
			}
			return itens;
		},

		headers_manual() {
			let itens = [
				{ text: "Descrição do Item", align: "start", sortable: false, value: "descricao" },
				{ text: "Peso Unit.", align: "end", sortable: false, value: "peso_unitario" },
				{ text: "VUCV", align: "end", sortable: false, value: "vucv" },
				{ text: "Quantidade", align: "end", sortable: false, value: "quantidade" },
			];
			if (this.blExibeCide) {
				itens.push({ text: "Cide Combustiveis", align: "end", sortable: false, value: "cide_combustiveis.valor" });
			}
			if (this.blExibeAntidumping) {
				itens.push({ text: "Antidumping", align: "end", sortable: false, value: "antidumping" });
			}
			itens.push({ text: "VMCV do Item", align: "end", sortable: false, value: "vmcv" });
			itens.push({ text: 'Ações', sortable: false, value: "actions" });
			return itens;
		},
	},

	watch: {
		"consulta.codigo_estado"() {
			this.filtroIcms();
		},
		"adicaoEscolhida.tributacoes.cide_combustiveis.valor"(val) {
			if (val !== null && val !== "") {
				this.mostrar_cide = true;
			}
		},
		"adicaoEscolhida.antidumping"(val) {
			if (val !== null && val !== "") {
				this.mostra_antidumping = true;
			}
		},
		"aliquotas.ICMS"(val) {
			if (val.ICMS) {
				if (val.FUNDO.aliquota.length > 0) {
					this.fundoAliquota = val.FUNDO.aliquota[0];
				}
				if (val.ICMS.aliquota.length > 0) {
					this.icmsAliquota = val.ICMS.aliquota[0];
				}
				this.icmsTipoTributacao = val.TIPO_TRIBUTACAO;
			}
		},
		loader() {
			const l = this.loader;
			this[l] = !this[l];

			setTimeout(() => (this[l] = false), 6000);

			this.loader = null;
		},
		mostraLinha(val) {
			if (val == true) {
				this.dados = {
					id_adicao: "",
					descricao: "",
					quantidade: "",
					peso_unitario: "",
					valor_unitario: "",
					vmcv: "",
					antidumping: "",
					vucv: "",
					cide_combustiveis: {
						valor: ""
					}
				};
			}
		},
		selected(val) {
			if (val.length == 0 && this.dialogDelete == true) {
				this.dialogDelete = false;
			}
		},

	},

	created() {
		this.historico = this.listaItens;
		this.filtroIcms();
		this.mostraCideOuAntidumping();
		this.valorPadraoCideVazio();
	},

	filters: {
		truncate: function (text, length, suffix) {
			if (
				typeof text !== "undefined" &&
				text !== null &&
				text !== "" &&
				text.length > length
			) {
				return text.substring(0, length) + suffix;
			} else {
				return text;
			}
		},
	},

	methods: {
		obterAliquotasEAlertasIcms() {
			this.$store.dispatch("aliquotas/limparAliquotasEAlertasIcms");
			this.$store.dispatch("aliquotas/obterAliquotasIcms", {
				ncm: this.adicaoEscolhida.ncm.numero,
				pais: this.adicaoEscolhida.id_pais_exportador,
				uf: this.consulta.codigo_estado,
			});
		},
		valorPadraoCideVazio() {
			let valor_cide
			this.listaItens.forEach(h => {
				valor_cide = h.cide_combustiveis
				if (valor_cide.valor == null) {
					return valor_cide.valor = 0.00000
				}
			});
		},
		mostraCideOuAntidumping() {
			if (
				typeof this.adicaoEscolhida.tributacoes.cide_combustiveis !==
				"undefined" &&
				this.adicaoEscolhida.tributacoes.cide_combustiveis !== null &&
				this.adicaoEscolhida.tributacoes.cide_combustiveis !== "" &&
				typeof this.adicaoEscolhida.tributacoes.cide_combustiveis.valor !==
				"undefined" &&
				this.adicaoEscolhida.tributacoes.cide_combustiveis.valor !== null &&
				this.adicaoEscolhida.tributacoes.cide_combustiveis.valor !== "" &&
				this.adicaoEscolhida.tributacoes.cide_combustiveis.valor > 0
			) {
				this.mostrar_cide = true;
			} else {
				this.mostrar_cide = false;
			}
			if (
				typeof this.adicaoEscolhida.antidumping !== "undefined" &&
				this.adicaoEscolhida.antidumping !== null &&
				this.adicaoEscolhida.antidumping !== ""
			) {
				this.mostra_antidumping = true;
			} else {
				this.mostra_antidumping = false;
			}

		},

		filtroIcms() {
			//Filtra itens apresentados no select caso o Estado selecionado seja SP
			let itens = this.tipo_tributacao;

			if (
				this.consulta.codigo_estado == null ||
				this.consulta.codigo_estado == ""
			) {
				this.icms_aux = [];
				return;
			}

			if (itens) {
				this.icms_aux = itens.filter((el) => {
					return (
						el.id == this.$INTEGRAL.ID ||
						el.id == this.$ISENCAO.ID ||
						el.id == this.$SUBST_TRIBUTARIA.ID ||
						el.id == this.$DIF_ICMS.ID ||
						el.id == this.$NAO_INCIDENCIA.ID ||
						el.id == this.$SUSPENSAO.ID ||
						el.id == this.$REDUCAO_BC.ID ||
						(el.id == this.$ISENCAO_PARCIAL.ID && this.consulta.codigo_estado == 'SP')
					);
				});
			}
		},

		titulo_tributo(key) {
			const titulos = {
				cide_combustiveis: 'CIDE Combustíveis',
				cofins: 'COFINS',
				fundo_combate_pobreza: 'Fundo de Combate à Pobreza',
				icms: 'ICMS',
				imposto_importacao: 'II',
				ipi: 'IPI',
				pis: 'PIS'
			};
			return titulos[key] || '';
		},

		formataCideSlider() {
			if (this.adicaoEscolhida.tributacoes.cide_combustiveis.valor !== null) {
				return this.moedaBR(this.adicaoEscolhida.tributacoes.cide_combustiveis.valor);
			} else {
				return 'Não possui incidência';
			}
		},
		formataAliquota(value) {
			if (value == null) {
				return "";
			} else {
				return value
					.toString()
					.match(/^-?\d+(?:\.\d{0,2})?/)[0]
					.replace(".", ",") + "%";
			}
		},

		quantidadeItens() {
			//Realiza a soma da quantidade de itens da adição para exibição na faixa superior do detalhe da adição
			let qtItens = 0;
			this.historico.forEach((h) => {
				if (h.id != this.historico.id) {
					qtItens += parseFloat(h.quantidade);
				}
			});
			return parseFloat(qtItens).toFixed(5).replace(".", ",");
		},

		pegandoVmcv() {
			let vmcvTotal = 0;

			if (typeof this.historico !== "undefined" && this.historico !== null && this.historico !== '' && this.historico.length > 0) {
				this.historico.forEach(element => {
					if (typeof element.vmcv !== 'undefined' && element.vmcv !== null && element.vmcv !== '') {
						vmcvTotal += (element.vucv * element.quantidade);
					}
				});
			}
			return vmcvTotal;
		},

		pegandoPesoItens() {
			let pesoItens = 0;
			if (this.historico !== null && this.historico.length > 0) {
				this.historico.forEach(element => {
					if (element.peso_unitario !== null ) {
						pesoItens = pesoItens + parseFloat(element.peso_unitario) * parseFloat(element.quantidade);
					}
				});
			}
			return pesoItens;
		},
		pegandoPesoTotalAdicao() {
			let pesoTotal = 0;
			if (
				typeof this.adicaoEscolhida.peso_liquido_total !== "undefined" &&
				this.adicaoEscolhida.peso_liquido_total !== null &&
				this.adicaoEscolhida.peso_liquido_total !== '' &&
				this.adicaoEscolhida.peso_liquido_total > 0
			) {
				pesoTotal = parseFloat(this.adicaoEscolhida.peso_liquido_total);
			}
			return pesoTotal;
		},

		editarAdicao(id_adicao) {

			//pegar dados da adição
			this.$root.$api.get("/adicao/carregar/?id_adicao=" + id_adicao)
				.then(async (response) => {
					if (response.status == "200") {
						//popular incluir adição
						let form = new Form();
						form.carregarNovaAdicao()
						await form.carregandoDadosEditarAdicao(response.data.resultado)
							.then(() => {
								this.handleModalAdicao = true;
							})
					} else {
						Object.bus.$emit("structure", {
							type: "error",
							message: "Não foi possível carregar os dados da adição.",
							timeout: 1000,
						});
						this.handleModalAdicao = false;
					}
				}).catch((err) => {
					console.log('erro #2', err);
					Object.bus.$emit("structure", {
						type: "error",
						message: "Não foi possível carregar os dados da adição.",
						timeout: 1000,
					});
					this.handleModalAdicao = false;
				});
		},
		showDeletarAdicao(adicaoEscolhida) {
			this.adicaoToDelete = adicaoEscolhida
			this.dialogDeleteAdicao = !this.dialogDeleteAdicao
		},
		deletarAdicao() {
			let body = {
				lista_ids: [this.adicaoToDelete]
			}
			this.$store.dispatch('adicao/deletar', body).then(() => {
				// colocar rota retorna consulta
				this.closeDelete();
			});
		},
		closeDelete() {
			this.dialogDeleteAdicao = false;
		},
		voltar() {
			this.$emit("voltar");
		},
		atualizarDados() {
			this.$store.dispatch('adicoes/carregarAdicoes', this.consulta.id);
			this.listarItem();
		},
		novoItem() {
			let itemNovo = Object.assign({}, this.itemDefault);
			this.$refs.modalEditarItem.carregandoDados(itemNovo);
			this.handleAbrirItem = true;
		},
		editarItem(item) {
			let itemEditar = Object.assign({}, item);
			if (itemEditar.cide_combustiveis == null) {
				itemEditar.cide_combustiveis = { valor: "" }
			}
			this.$refs.modalEditarItem.carregandoDados(itemEditar);
			this.handleAbrirItem = true;
		},
		deleteItemConfirm() {
			let lista_ids = [];
			this.selected.forEach(function (element) {
				lista_ids.push(element.id);
			});
			this.deleteTodos(lista_ids);
		},
		deleteTodos(lista_ids) {
			let body = {
				"lista_ids": lista_ids
			}
			this.$root.$api.delete("/item/deletar", body)
				.then((response) => {
					if (response.status == '200') {
						this.listarItem();
						this.dialogDelete = false;
						this.selected = [];

						Object.bus.$emit("structure", {
							message: lista_ids.length > 1 ? "Itens excluídos com sucesso." : "Item excluído com sucesso.",
							type: "success",
							timeout: 2000,
						});

						//carregar tabela adicao
						this.$store.dispatch('adicoes/carregarAdicoes', this.consulta.id);
						this.listarItem();
					}
				}).catch(() => {
					Object.bus.$emit("structure", {
						type: "error",
						message: "Não foi possivel excluir o(s) item(ns)",
						timeout: 1000,
					});
				});
		},
		deleteItens() {
			this.excluirMultiplos = true;
			this.editedIndex = -1;
			this.dialogDelete = true;
		},
		listarItem() {
			this.$root.$api.get("/item/listar/?id_adicao=" + this.adicaoEscolhida.id)
				.then(async (response) => {
					let itens = response.data.resultado
					this.historico = itens;
					this.$store.commit('itens/setListaItens', itens)
				});
		},
		async salvarItemXml(idAdicao) {
			this.$v.$touch();
			if (this.$v.$invalid) {
				return;
			}

			let response = await this.$root.$api.put("/adicao/xml", {
				icms: this.adicaoEscolhida.tributacoes.icms,
				fundo_combate_pobreza: this.adicaoEscolhida.tributacoes.fundo_combate_pobreza,
				itens: this.historico,
				id_declaracao: this.consulta.id,
				id_adicao: idAdicao
			});

			if (response.status == "200") {
				Object.bus.$emit("structure", {
					message: "Adição editada com sucesso.",
					type: "success",
					timeout: 2000,
				});

				this.listarItem();
				this.$store.dispatch('adicoes/carregarAdicoes', this.consulta.id, this.adicaoEscolhida.id);
				setTimeout(() => {
					this.voltar();
				}, 1000);
			}
		},
		showDeleteDialog(item) {
			this.excluirMultiplos = false
			this.itemToDelete = item
			this.dialogDelete = !this.dialogDelete
		},
		deleteItem() {
			let lista_ids = [this.itemToDelete.id];
			this.deleteTodos(lista_ids);
		},
		obterSiglaUnidadeMedida() {
			let sigla = this.lista_unidade_medida.filter(u => {
				return u.id == this.adicaoEscolhida.id_unidade_medida
			})
			return sigla[0] && sigla[0].sigla_unidade ? sigla[0].sigla_unidade : 'UN'
		},
	}

};
