import { mapGetters } from "vuex";
import formataMoedaMixin from "@/mixins/formataMoedaMixin";

export default {
  name: "Tributos",

  components: {},

  validations: {
    // II
    "adicao.tributacoes.ii.id_tipo_tributacao": {
      checked() {
        return this.adicao.tributacoes.ii.id_tipo_tributacao;
      },
    },
    "adicao.tributacoes.ii.aliquota": {
      checked() {
        let aliquota = this.adicao.tributacoes.ii.aliquota;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      },
    },

    // IPI
    "adicao.tributacoes.ipi.id_tipo_tributacao": {
      checked() {
        return this.adicao.tributacoes.ipi.id_tipo_tributacao;
      },
    },
    "adicao.tributacoes.ipi.aliquota": {
      checked() {
        if (this.blIpiDisabled) {
          return true;
        }
        let aliquota = this.adicao.tributacoes.ipi.aliquota;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      }
    },

    // PIS
    "adicao.tributacoes.pis.id_tipo_tributacao": {
      checked() {
        return this.adicao.tributacoes.pis.id_tipo_tributacao;
      },
    },
    "adicao.tributacoes.pis.aliquota": {
      checked() {
        if (this.blPisCofinsDisabled) {
          return true;
        }
        let aliquota = this.adicao.tributacoes.pis.aliquota;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      },
    },

    // COFINS
    "adicao.tributacoes.cofins.id_tipo_tributacao": {
      checked() {
        return this.adicao.tributacoes.cofins.id_tipo_tributacao;
      },
    },
    "adicao.tributacoes.cofins.aliquota": {
      checked() {
        if (this.blPisCofinsDisabled) {
          return true;
        }
        let aliquota = this.adicao.tributacoes.cofins.aliquota;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      },
    },

    // CIDE Combustíveis
    "adicao.tributacoes.cide_combustiveis.valor": {
      checked() {
        if (this.checkCide || !this.blExibeCide) {
          return true;
        }
        return this.adicao.tributacoes.cide_combustiveis.valor > 0;
      },
    },

    // FUNDO
    "adicao.tributacoes.fundo_combate_pobreza.aliquota": {
      checked() {
        /*
        if (!this.blExibeFundo) {
          return true;
        }
        let icmsFundo = this.adicao.tributacoes.fundo_combate_pobreza.aliquota;
        if (icmsFundo == null || icmsFundo == "") {
          return false;
        }
        */
        return true;
      }
    },

    // ICMS
    "adicao.tributacoes.icms.id_tipo_tributacao": {
      checked() {
        let icmsTipoTributacao = this.adicao.tributacoes.icms.id_tipo_tributacao;
        if (icmsTipoTributacao == null || icmsTipoTributacao == "") {
          return false;
        }
        return true;
      },
    },
    "adicao.tributacoes.icms.aliquota_icms": {
      checked() {
        let aliquota = this.adicao.tributacoes.icms.aliquota_icms;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      },
    },
    "adicao.tributacoes.icms.base_calculo_st": {
      checked() {
        if (this.icmsTipoTributacao != this.$SUBST_TRIBUTARIA.ID) {
          return true;
        }
        let aliquota = this.adicao.tributacoes.icms.base_calculo_st;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      }
    },
    "adicao.tributacoes.icms.aliquota_icms_st": {
      checked() {
        if (this.icmsTipoTributacao != this.$SUBST_TRIBUTARIA.ID) {
          return true;
        }
        let aliquota = this.adicao.tributacoes.icms.aliquota_icms_st;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      }
    },
    "adicao.tributacoes.icms.cod_cest": {
      checked() {
        if (this.icmsTipoTributacao != this.$SUBST_TRIBUTARIA.ID) {
          return true;
        }
        return this.adicao.tributacoes.icms.cod_cest != '';
      }
    },
    "adicao.tributacoes.icms.aliquota_icms_diferido": {
      checked() {
        if (this.icmsTipoTributacao != this.$DIF_ICMS.ID) {
          return true;
        }
        let aliquota = this.adicao.tributacoes.icms.aliquota_icms_diferido;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      }
    },
    "adicao.tributacoes.icms.percentual_isencao": {
      checked() {
        if (this.icmsTipoTributacao != this.$ISENCAO_PARCIAL.ID) {
          return true;
        }
        let aliquota = this.adicao.tributacoes.icms.percentual_isencao;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      }
    },
    "adicao.tributacoes.icms.reducao_base_calculo": {
      checked() {
        if (this.icmsTipoTributacao != this.$REDUCAO_BC.ID) {
          return true;
        }
        let aliquota = this.adicao.tributacoes.icms.reducao_base_calculo;
        if (!aliquota && aliquota != '0') {
          return false;
        }
        return true;
      },
    },
  },

  mixins: [
    formataMoedaMixin
  ],

  data() {
    return {
      checkCide: false,
      imposto_importacao_items: [],
      ipi_items: [],
      pis_items: [],
      cofins_items: [],
      icms_aux: [],
      tipo: 0,
      currency_config: {
        decimal: ",",
        thousands: ".",
        precision: 4,
        masked: false,
        allowBlank: false,
        min: Number.MIN_SAFE_INTEGER,
        max: Number.MAX_SAFE_INTEGER,
      },
      ncmOriginal: '',
    };
  },

  created() {
    // this.resetAdicao();
    this.filtro_imposto_importacao();
    this.filtro_ipi();
    this.filtro_pis();
    this.filtro_cofins();
    this.filtroIcms();
    this.ncmOriginal = '';
  },

  mounted() {
    if (
      this.adicao.id !== null &&
      this.adicao.id !== "" &&
      this.handleModalAdicao == true
    ) {
      this.ncmOriginal = this.adicao.ncm.numero;
      if (
        this.adicao.tributacoes.cide_combustiveis.valor == null ||
        this.adicao.tributacoes.cide_combustiveis.valor == ""
      ) {
        this.checkCide = true;
      } else {
        this.checkCide = false;
      }
    }
  },

  computed: {
    blExibeFundo() {
      return this.adicao.tributacoes.fundo_combate_pobreza.aliquota != null || this.aliquotas.ICMS.FUNDO?.alertas?.length > 0 || this.adicao.ncm.vigente == 0;
    },

    blExibeConsumidorFinal() {
      return this.consulta.codigo_estado == 'AM' && this.adicao.tributacoes.icms.id_tipo_tributacao != 7;
    },

    blExibeCide() {
      return (this.adicao.id > 0 && this.adicao.tributacoes.cide_combustiveis.valor != null) || this.aliquotas.CIDE?.alertas?.length > 0 || this.adicao.ncm.vigente == 0;
    },

    blIIDisabled() {
      return [1, 3, 5, 9, 12].indexOf(this.adicao.tributacoes.ii.id_tipo_tributacao) != -1;
    },

    blIpiDisabled() {
      return [1, 3, 5, 9, 12].indexOf(this.adicao.tributacoes.ipi.id_tipo_tributacao) != -1;
    },

    blPisCofinsDisabled() {
      return [1, 3, 5, 9, 12].indexOf(this.adicao.tributacoes.pis.id_tipo_tributacao) != -1;
    },

    iiTipoErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.ii.id_tipo_tributacao"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.ii.id_tipo_tributacao"].checked && errors.push("Tipo de Tributação obrigatório.");
      return errors;
    },

    iiAliquotaErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.ii.aliquota"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.ii.aliquota"].checked && errors.push("Aliquota obrigatória.");
      return errors;
    },

    ipiTipoErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.ipi.id_tipo_tributacao"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.ipi.id_tipo_tributacao"].checked && errors.push("Tipo de Tributação obrigatório.");
      return errors;
    },

    ipiAliquotaErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.ipi.aliquota"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.ipi.aliquota"].checked && errors.push("Aliquota obrigatória.");
      return errors;
    },

    cideTipoErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.cide_combustiveis.valor"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.cide_combustiveis.valor"].checked && errors.push("Valor obrigatório.");
      return errors;
    },

    pisTipoErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.pis.id_tipo_tributacao"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.pis.id_tipo_tributacao"].checked && errors.push("Tipo de Tributação obrigatório.");
      return errors;
    },

    pisAliquotaErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.pis.aliquota"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.pis.aliquota"].checked && errors.push("Aliquota obrigatória.");
      return errors;
    },

    cofinsTipoErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.cofins.id_tipo_tributacao"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.cofins.id_tipo_tributacao"].checked && errors.push("Tipo de Tributação obrigatório.");
      return errors;
    },

    cofinsAliquotaErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.cofins.aliquota"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.cofins.aliquota"].checked && errors.push("Aliquota obrigatória.");
      return errors;
    },

    icmsFundoErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.fundo_combate_pobreza.aliquota"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.fundo_combate_pobreza.aliquota"].checked && this.blExibeFundo && errors.push("Valor obrigatório.");
      return errors;
    },

    icmsAliquotaErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.icms.aliquota_icms"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.icms.aliquota_icms"].checked && errors.push("Aliquota é obrigatória.");
      return errors;
    },

    icmsTipoErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.icms.id_tipo_tributacao"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.icms.id_tipo_tributacao"].checked && errors.push("Tipo de Tributação obrigatório.");
      return errors;
    },

    icmsAliquotaStErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.icms.aliquota_icms_st"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.icms.aliquota_icms_st"].checked && errors.push("Aliquota é obrigatória.");
      return errors;
    },

    icmsBaseCalculoStErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.icms.base_calculo_st"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.icms.base_calculo_st"].checked && errors.push("Aliquota é obrigatória.");
      return errors;
    },

    icmsPercentualIsencaoErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.icms.percentual_isencao"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.icms.percentual_isencao"].checked && errors.push("Aliquota é obrigatória.");
      return errors;
    },

    icmsReducaoBaseErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.icms.reducao_base_calculo"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.icms.reducao_base_calculo"].checked && errors.push("Aliquota é obrigatória.");
      return errors;
    },

    icmsAliquotaIcmsDiferidoErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.icms.aliquota_icms_diferido"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.icms.aliquota_icms_diferido"].checked && errors.push("Aliquota é obrigatória.");
      return errors;
    },

    cestErrors() {
      const errors = [];
      if (!this.$v["adicao.tributacoes.icms.cod_cest"].$dirty)
        return errors;
      !this.$v["adicao.tributacoes.icms.cod_cest"].checked && errors.push("Valor obrigatório.");
      return errors;
    },

    icmsTipoTributacao: {
      get() {
        if (this.adicao.tributacoes.icms.id_tipo_tributacao) {
          return this.adicao.tributacoes.icms.id_tipo_tributacao;
        }
        let tipo = 2;
        this.$store.commit('adicao/setIcmsIdTipoTributacao', tipo);
        this.$store.commit('aliquotas/setTipoIcms', tipo);
        return tipo;
      },
      set(tipo) {
        this.$store.commit('adicao/setIcmsIdTipoTributacao', tipo);
        this.$store.commit('aliquotas/setTipoIcms', tipo);
      }
    },

    icmsAliquotas: {
      get() {
        return (this.aliquotas.ICMS.ICMS && this.aliquotas.ICMS.ICMS.aliquota.length > 0) ?
          this.aliquotas.ICMS.ICMS.aliquota :
          [0];
      },
      set(icms) {
        return icms;
      }
    },

    icmsAliquota: {
      get() {
        if (this.adicao.tributacoes.icms.aliquota_icms) {
          return this.adicao.tributacoes.icms.aliquota_icms;
        }
        let aliquota = this.icmsAliquotas[0];
        this.$store.commit('adicao/setIcmsAliquota', aliquota);
        return aliquota;
      },
      set(icms) {
        if (icms && icms.toString().indexOf(',') != -1) {
          icms = icms.replace('.', '');
          icms = icms.replace(',', '.');
        }
        this.$store.commit('adicao/setIcmsAliquota', icms);
      }
    },

    cestItens() {
      return this.aliquotas.ICMS.ST && this.aliquotas.ICMS.ST.cest ? this.aliquotas.ICMS.ST.cest : [this.adicao.tributacoes.icms.cod_cest];
    },

    iiTipoTributacao: {
      get() {
        if (this.adicao.tributacoes.ii.id_tipo_tributacao) {
          return this.adicao.tributacoes.ii.id_tipo_tributacao;
        }
        let tipo = 2;
        this.$store.commit('adicao/setImpostoImportacaoTipoTributacao', tipo);
        return tipo;
      },
      set(tipo) {
        this.$store.commit('adicao/setImpostoImportacaoTipoTributacao', tipo);
      }
    },

    iiAliquotas: {
      get() {
        return (this.aliquotas.II.aliquota && this.aliquotas.II.aliquota.length > 0) ?
          this.aliquotas.II.aliquota
          : [0];
      },
      set(ii) {
        return ii;
      }
    },

    iiAliquota: {
      get() {
        if (this.adicao.tributacoes.ii.aliquota) {
          return this.adicao.tributacoes.ii.aliquota;
        }
        let aliquota = this.iiAliquotas[0];
        this.$store.commit('adicao/setImpostoImportacao', {
          id_tipo_tributacao: this.adicao.tributacoes.ii.id_tipo_tributacao,
          aliquota: aliquota
        });
        return aliquota;
      },
      set(ii) {
        if (ii && ii.toString().indexOf(',') != -1) {
          ii = ii.replace('.', '');
          ii = ii.replace(',', '.');
        }
        this.$store.commit('adicao/setImpostoImportacao', {
          id_tipo_tributacao: this.adicao.tributacoes.ii.id_tipo_tributacao,
          aliquota: ii
        });
      }
    },

    ipiTipoTributacao: {
      get() {
        if (this.adicao.tributacoes.ipi.id_tipo_tributacao) {
          return this.adicao.tributacoes.ipi.id_tipo_tributacao;
        }
        let tipo = 2;
        this.$store.commit('adicao/setIpiTipoTributacao', tipo);
        return tipo;
      },
      set(tipo) {
        this.$store.commit('adicao/setIpiTipoTributacao', tipo);
      }
    },

    ipiAliquotas: {
      get() {
        let ipi = [0];
        if (this.aliquotas.IPI.aliquota && this.aliquotas.IPI.aliquota.length > 0) {
          let ipis = this.aliquotas.IPI.aliquota.filter(a => a != 'NT');
          if (ipis.length > 0) {
            ipi = ipis;
          }
        }
        return ipi;
      },
      set(ipi) {
        return ipi;
      }
    },

    ipiAliquota: {
      get() {
        if (this.adicao.tributacoes.ipi.aliquota) {
          return this.adicao.tributacoes.ipi.aliquota;
        }
        let aliquota = this.ipiAliquotas[0];
        this.$store.commit('adicao/setIpi', {
          id_tipo_tributacao: this.adicao.tributacoes.ipi.id_tipo_tributacao,
          aliquota: aliquota
        });
        return aliquota;
      },
      set(ipi) {
        if (ipi && ipi.toString().indexOf(',') != -1) {
          ipi = ipi.replace('.', '');
          ipi = ipi.replace(',', '.');
        }
        this.$store.commit('adicao/setIpi', {
          id_tipo_tributacao: this.adicao.tributacoes.ipi.id_tipo_tributacao,
          aliquota: ipi
        });
      }
    },

    pisTipoTributacao: {
      get() {
        if (this.adicao.tributacoes.pis.id_tipo_tributacao) {
          return this.adicao.tributacoes.pis.id_tipo_tributacao;
        }
        let tipo = 2;
        this.$store.commit('adicao/setPisTipoTributacao', tipo);
        return tipo;
      },
      set(tipo) {
        this.$store.commit('adicao/setPisTipoTributacao', tipo);
      }
    },

    pisAliquotas: {
      get() {
        let pis = [0];
        if (this.aliquotas.PIS_COFINS?.aliquota_pis?.length > 0) {
          pis = this.aliquotas.PIS_COFINS.aliquota_pis;
        }
        return pis;
      },
      set(pis) {
        return pis;
      }
    },

    pisAliquota: {
      get() {
        if (this.adicao.tributacoes.pis.aliquota) {
          return this.adicao.tributacoes.pis.aliquota;
        }
        let aliquota = this.pisAliquotas[0];
        this.$store.commit('adicao/setPis', {
          id_tipo_tributacao: this.adicao.tributacoes.pis.id_tipo_tributacao,
          aliquota: aliquota
        });
        return aliquota;
      },
      set(pis) {
        if (pis && pis.toString().indexOf(',') != -1) {
          pis = pis.replace('.', '');
          pis = pis.replace(',', '.');
        }
        this.$store.commit('adicao/setPis', {
          id_tipo_tributacao: this.adicao.tributacoes.pis.id_tipo_tributacao,
          aliquota: pis
        });
      }
    },

    cofinsAliquotas: {
      get() {
        let cofins = [0];
        if (this.aliquotas.PIS_COFINS?.aliquota_cofins?.length > 0) {
          cofins = this.aliquotas.PIS_COFINS.aliquota_cofins;
        }
        return cofins;
      },
      set(cofins) {
        return cofins;
      }
    },

    cofinsAliquota: {
      get() {
        if (this.adicao.tributacoes.cofins.aliquota) {
          return this.adicao.tributacoes.cofins.aliquota;
        }
        let aliquota = this.cofinsAliquotas[0];
        this.$store.commit('adicao/setCofins', {
          id_tipo_tributacao: this.adicao.tributacoes.cofins.id_tipo_tributacao,
          aliquota: aliquota
        });
        return aliquota;
      },
      set(cofins) {
        if (cofins && cofins.toString().indexOf(',') != -1) {
          cofins = cofins.replace('.', '');
          cofins = cofins.replace(',', '.');
        }
        this.$store.commit('adicao/setCofins', {
          id_tipo_tributacao: this.adicao.tributacoes.cofins.id_tipo_tributacao,
          aliquota: cofins
        });
      }
    },

    fundoAliquotas: {
      get() {
        let fundo = [];
        if (this.aliquotas.ICMS.FUNDO && this.aliquotas.ICMS.FUNDO.aliquota.length > 0) {
          fundo = this.aliquotas.ICMS.FUNDO.aliquota;
        }
        return fundo;
      },
      set(fundo) {
        return fundo;
      }
    },

    fundoAliquota: {
      get() {
        if (this.adicao.tributacoes.fundo_combate_pobreza.aliquota >= 0) {
          return this.adicao.tributacoes.fundo_combate_pobreza.aliquota;
        }
        let aliquota = null;
        if (this.fundoAliquotas[0]) {
          aliquota = this.fundoAliquotas[0]
          this.$store.commit('adicao/setFundoCombatePobreza', {
            aliquota: aliquota
          });
        }
        return aliquota;
      },
      set(fundo) {
        if (fundo && fundo.toString().indexOf(',') != -1) {
          fundo = fundo.replace('.', '');
          fundo = fundo.replace(',', '.');
        }
        this.$store.commit('adicao/setFundoCombatePobreza', {
          aliquota: fundo
        });
      }
    },

    ...mapGetters("adicao", { adicao: "getState" }),
    ...mapGetters("consulta", { consulta: "getState" }),
    ...mapGetters("informacoesPadrao", { tipo_tributacao: "getTipoTributacao" }),
    ...mapGetters("modalAdicoes", { handleModalAdicao: "getModalAdicoes" }),
    ...mapGetters("aliquotas", { aliquotas: "getState" }),
  },

  watch: {
    "consulta.codigo_estado"() {
      this.filtroIcms();
    },

    handleModalAdicao(val) {
      if (val == true) {
        if (this.adicao.id == null || this.adicao.id == "") {
          this.resetAdicao();
        } else {
          this.checkCide = this.adicao.tributacoes.cide_combustiveis.valor == null || this.adicao.tributacoes.cide_combustiveis.valor == ""
          this.ncmOriginal = this.adicao.ncm.numero;
        }

        this.limpaErros();
      }
    },

    icmsTipoTributacao(val) {
      if (
        val == this.$SUBST_TRIBUTARIA.ID ||
        val == this.$ISENCAO_PARCIAL.ID ||
        val == this.$REDUCAO_BC.ID ||
        val == this.$DIF_ICMS.ID
      ) {
        this.$emit("downScroll");
      }
    },

    "adicao.tributacoes.ii.id_tipo_tributacao"(val) {
      let aliquota = this.adicao.tributacoes.ii.aliquota;
      let isentos = [1, 3, 5, 9, 12];

      if (isentos.indexOf(val) != -1) {
        aliquota = '0,0'
      }

      this.iiAliquota = aliquota;
    },

    "adicao.tributacoes.ipi.id_tipo_tributacao"(val) {
      let aliquota = this.adicao.tributacoes.ipi.aliquota;
      let isentos = [1, 3, 5, 9, 12];

      if (isentos.indexOf(val) != -1) {
        aliquota = '0,0'
      }

      this.ipiAliquota = aliquota;
    },

    "adicao.tributacoes.pis.id_tipo_tributacao"(val) {
      this.adicao.tributacoes.cofins.id_tipo_tributacao = val;

      let aliquotaPis = this.adicao.tributacoes.pis.aliquota;
      let aliquotaCofins = this.adicao.tributacoes.cofins.aliquota;
      let isentos = [1, 3, 5, 12];

      if (isentos.indexOf(val) != -1) {
        aliquotaPis = aliquotaCofins = '0,0'
      }

      this.pisAliquota = aliquotaPis;
      this.cofinsAliquota = aliquotaCofins;
    },
  },

  methods: {
    limpaErros() {
      this.$v.$reset();
    },

    verificaFormularioTributos() {
      this.$v.$touch();

      return !this.$v.$invalid;
    },

    filtro_imposto_importacao() {
      let itens = this.tipo_tributacao;
      let filtro = [];
      filtro = itens.filter((el) => {
        return (
          el.id == this.$ISENCAO.ID ||
          el.id == this.$INTEGRAL.ID ||
          el.id == this.$IMUNIDADE.ID ||
          el.id == this.$NAO_INCIDENCIA.ID ||
          el.id == this.$REDUCAO.ID ||
          el.id == this.$SUSPENSAO.ID ||
          el.id == this.$ACORDO_COMERCIAL.ID
        );
      });
      this.imposto_importacao_items = filtro;
    },

    filtro_ipi() {
      let itens = this.tipo_tributacao;
      let filtro = [];
      filtro = itens.filter((el) => {
        return (
          el.id == this.$ISENCAO.ID ||
          el.id == this.$INTEGRAL.ID ||
          el.id == this.$IMUNIDADE.ID ||
          el.id == this.$REDUCAO.ID ||
          el.id == this.$NAO_TRIBUTAVEL.ID ||
          el.id == this.$SUSPENSAO.ID
        );
      });
      this.ipi_items = filtro;
    },

    filtro_pis() {
      let itens = this.tipo_tributacao;
      let filtro = [];
      filtro = itens.filter((el) => {
        return (
          el.id == this.$ISENCAO.ID ||
          el.id == this.$INTEGRAL.ID ||
          el.id == this.$IMUNIDADE.ID ||
          el.id == this.$NAO_INCIDENCIA.ID ||
          el.id == this.$REDUCAO.ID ||
          el.id == this.$SUSPENSAO.ID
        );
      });
      this.pis_items = filtro;
    },

    filtro_cofins() {
      let itens = this.tipo_tributacao;
      let filtro = [];
      filtro = itens.filter((el) => {
        return (
          el.id == this.$ISENCAO.ID ||
          el.id == this.$INTEGRAL.ID ||
          el.id == this.$IMUNIDADE.ID ||
          el.id == this.$NAO_INCIDENCIA.ID ||
          el.id == this.$REDUCAO.ID ||
          el.id == this.$SUSPENSAO.ID
        );
      });
      this.cofins_items = filtro;
    },

    filtroIcms() {
      //Filtra itens apresentados no select caso o Estado selecionado seja SP
      let itens = this.tipo_tributacao;

      if (
        this.consulta.codigo_estado == null ||
        this.consulta.codigo_estado == ""
      ) {
        this.icms_aux = [];
        return;
      }

      this.icms_aux = itens.filter((el) => {
        return (
          el.id == this.$INTEGRAL.ID ||
          el.id == this.$ISENCAO.ID ||
          el.id == this.$SUBST_TRIBUTARIA.ID ||
          el.id == this.$DIF_ICMS.ID ||
          el.id == this.$NAO_INCIDENCIA.ID ||
          el.id == this.$SUSPENSAO.ID ||
          el.id == this.$REDUCAO_BC.ID ||
          (el.id == this.$ISENCAO_PARCIAL.ID && this.consulta.codigo_estado == 'SP')
        );
      });
    },

    resetAdicao() {
      this.adicao.tributacoes.ii.aliquota = '';
      this.adicao.tributacoes.ii.id_tipo_tributacao = '';

      this.adicao.tributacoes.ipi.aliquota = '';
      this.adicao.tributacoes.ipi.id_tipo_tributacao = '';

      this.adicao.tributacoes.pis.aliquota = '';
      this.adicao.tributacoes.pis.id_tipo_tributacao = '';

      this.adicao.tributacoes.cofins.aliquota = '';
      this.adicao.tributacoes.cofins.id_tipo_tributacao = '';

      this.adicao.tributacoes.fundo_combate_pobreza.aliquota = '';

      this.checkCide = '';
      this.adicao.tributacoes.cide_combustiveis.valor = '';

      this.adicao.tributacoes.icms.id_tipo_tributacao = '';
      this.adicao.tributacoes.icms.aliquota_icms = '';
      this.adicao.tributacoes.icms.aliquota_icms_st = '';
      this.adicao.tributacoes.icms.base_calculo_st = '';
      this.adicao.tributacoes.icms.percentual_isencao = '';
      this.adicao.tributacoes.icms.reducao_base_calculo = '';
      this.adicao.tributacoes.icms.aliquota_icms_diferido = '';
      this.adicao.tributacoes.icms.bl_consumidor_final = '';

      this.adicao.descontos = '';
    },

    obterAliquotasEAlertas() {
      this.$store.dispatch("adicao/resetarTributosAdicao");
      this.$store.dispatch("aliquotas/limparAliquotasEAlertas");
      this.$store.dispatch("aliquotas/obterAliquotas", {
        ncm: this.adicao.ncm.numero,
        pais: this.adicao.id_pais_exportador,
        uf: this.consulta.codigo_estado,
      });
    },
  },
};
